import React, { useContext } from "react";
import styles from "./ShoppingCarPage.module.css";
import User from "../../components/user/user";
import { CartItem } from "../../interfaceList";
import axios from "axios";

interface props {
  items: CartItem[];
  buyCart: () => void;
}

const ShoppingCarPage: React.FC<props> = ({ items, buyCart }) => {
  const num = 0;
  const sum = items.reduce((a, b) => a + b.goods.price * b.quantity, num);

  const buycart = () => {
    buyCart();
  };

  const deletegoods = (
    event: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ) => {
    const id = event.target.getAttribute("data-id");
    const name = event.currentTarget.getAttribute("data-name");
    //到时候直接将删除数据给数据库，然后用 副作用 钩子刷新购物车
    try {
      axios("http://localhost:8000/goods/delGoods", { id, name });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <User />
      <div className={styles.container}>
        <table className={styles.table} border={1}>
          <tr>
            <th>样式</th>
            <th>商品id</th>
            <th>商品名称</th>
            <th>商品价格</th>
            <th>数量</th>
          </tr>
          {items.map((i) => (
            <tr>
              <td className={styles.td}>
                <img src={`${i.goods.img}`}></img>
              </td>
              <td>{i.goods.id}</td>
              <td>{i.goods.name}</td>
              <td>{i.goods.price}</td>
              <td>{i.quantity}</td>
              <td>
                <button
                  data-id={i.goods.id}
                  data-name={i.goods.name}
                  onClick={(event) => deletegoods(event)}
                >
                  移除
                </button>
              </td>
            </tr>
          ))}
        </table>
        <div className={styles.floatLabel}>
          <div className={styles.price}>{sum}</div>
          <a className={styles.buy} onClick={() => buycart()}>
            buy
          </a>
        </div>
      </div>
    </div>
  );
};

export default ShoppingCarPage;

