import React, { createContext, useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import styles from "./App.module.css";
import ShoppingCar from "./page/shoppingPage/ShoppingPage";
import LoginPage from "./page/loginPage/LoginPage";
import RegisterPage from "./page/registerPage/RegisterPage";
import ShoppingCarPage from "./page/shoppingCarPage/ShoppingCarPage";
import { Goods, CartItem } from "./interfaceList";

const goods: Goods = {
  id: 1,
  name: "robot",
  price: 123,
  img: "https://robohash.org/1",
};

const defaultContext: CartItem[] = [
  {
    goods: goods,
    quantity: 1,
  },
];

export const appContext = createContext();

function App() {
  const [Items, setItems] = useState<CartItem>();

  const addToCart = (goods: Goods) => {
    const existgoods = defaultContext.find((i) => {
      i.goods.id == goods.id;
    });

    if (existgoods) {
      defaultContext.map((i) => {
        if (i.goods.id == goods.id) {
          i.quantity++;
        }
      });
    } else {
      defaultContext.push({ goods, quantity: 1 });
    }
  };

  return (
    <appContext.Provider value={{ Items, addToCart }}>
      <div className={styles.app}>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<ShoppingCar />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/shoppingcart" element={<ShoppingCarPage />} />
          </Routes>
        </BrowserRouter>
      </div>
    </appContext.Provider>
  );

