            <Route path="/register" element={<RegisterPage />} />
            <Route
              path="/shoppingcart"
              element={<ShoppingCarPage cartItems={cartItems.map((i) => i)} />}
            />
          </Routes>
        </BrowserRouter>
      </div>
    </appContext.Provider>
  );
}

export default App;
