import React, { useState } from "react";
import styles from "./App.module.css";
import ShoppingCar from "./page/shoppingPage/ShoppingPage";
import LoginPage from "./page/loginPage/LoginPage";
import RegisterPage from "./page/registerPage/RegisterPage";
import ShoppingCarPage from "./page/shoppingCarPage/ShoppingCarPage";

/** 商品 */
interface Goods {
  id: number;
  name: string;
  price: number;
  img: string;
}

/** 购物车元素 */
interface CartItem {
  goods: Goods;
  quantity: number;
}

function App() {
  //购物车数组
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  //添加购物车
  const addToCart = (goods: Goods) => {
    const existingCartItem = cartItems.find((i) => i.goods.id === goods.id);

    if (existingCartItem) {
      // 购物车中已存在这个商品
      const updatedCartItems = cartItems.map((item) => {
        if (item.goods.id !== goods.id) {
          return item;
        }

        // 找到了匹配的商品
        return {
          ...item,
          quantity: item.quantity + 1,
        };
      });

      setCartItems(updatedCartItems);
    } else {
      // 购物车中不存在这个商品
      setCartItems([
        ...cartItems,
        {
          goods,
          quantity: 1,
        },
      ]);
    }
  };

  //获取子组件数据
  const handleDateFromChild = (data: any) => {
    console.log(data);
  };

  return (
    <div className={styles.app}>
      <ShoppingCar ondata={handleDateFromChild} />
      {/* <LoginPage /> */}
      {/* <RegisterPage /> */}
      {/* <ShoppingCarPage goods={cartItems.map((i) => i)} /> */}
    </div>
  );
}

export default App;
