function App() {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);

  const addToCart = (goods: Goods) => {
    const existingCartItem = cartItems.find((i) => i.goods.id === goods.id);

    if (existingCartItem) {
      // 购物车中已存在这个商品
      const updatedCartItems = cartItems.map((item) => {
        if (item.goods.id !== goods.id) {
          return item;
        }

        // 找到了匹配的商品
        return {
          ...item,
          quantity: item.quantity + 1,
        };
      });

      setCartItems(updatedCartItems);
    } else {
      // 购物车中不存在这个商品
      setCartItems([
        ...cartItems,
        {
          goods,
          quantity: 1,
        },
      ]);
    }
  };

  const buyCart = () => {
    setCartItems([]);
  };

  const deleteGoods = (goods: Goods) => {
    setCartItems([]);
  };

  return (
    <div className={styles.app}>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Navigate to="/goods" />} />
          <Route
            path="/goods"
            element={<ShoppingCar addToCart={addToCart} />}
          />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route
            path="/shoppingcart"
            element={
              <ShoppingCarPage
                items={cartItems}
                buyCart={buyCart}
                deleteGoods={deleteGoods}
              />
            }
          />
        </Routes>
      </BrowserRouter>
    </div>
  );

