import React, { useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import styles from "./App.module.css";
import ShoppingCar from "./page/shoppingPage/ShoppingPage";
import LoginPage from "./page/loginPage/LoginPage";
import RegisterPage from "./page/registerPage/RegisterPage";
import ShoppingCarPage from "./page/shoppingCarPage/ShoppingCarPage";

/** 商品 */
export interface Goods {
  id: number;
  name: string;
  price: number;
  img: string;
}

/** 购物车元素 */ export interface CartItem {
  goods: Goods;
  quantity: number;
}

function App() {
  const fakeData: Goods = {
    id: 1,
    name: "robot",
    price: 123,
    img: "https://robohash.org/1",
  };

  const item: CartItem = {
    goods: fakeData,
    quantity: 1,
  };

  //购物车数组
  const [cartItems, setCartItems] = useState<CartItem[]>([item]);
  //添加购物车
  const addToCart = (goods: Goods) => {
    const existingCartItem = cartItems.find((i) => i.goods.id === goods.id);

    if (existingCartItem) {
      // 购物车中已存在这个商品
      const updatedCartItems = cartItems.map((item) => {
        if (item.goods.id !== goods.id) {
          return item;
        }

        // 找到了匹配的商品
        return {
          ...item,
          quantity: item.quantity + 1,
        };
      });

      setCartItems(updatedCartItems);
    } else {
      // 购物车中不存在这个商品
      setCartItems([
        ...cartItems,
        {
          goods,
          quantity: 1,
        },
      ]);
    }
  };

  //获取子组件数据
  const handleDateFromChild = (data: any) => {
    console.log(data);
  };

  return (
    <div className={styles.app}>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<ShoppingCar />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route
            path="/shoppingcart"
            element={<ShoppingCarPage cartItems={cartItems.map((i) => i)} />}
          />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
