import React from "react";
import styles from "../loginPage/LoginPage.module.css";
import Title from "../../components/title/title";
import axios from "axios";

const LoginPage: React.FC = () => {
  React.MouseEventHandler;
  const inputUsername = (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault();
    const username = event.targer.username.value;
    const password = event.targer.password.value;
    console.log(username, password);
  };

  const handleOnClick = () => {
    axios.get("http://localhost:8000/login");
  };

  return (
    <div className={styles.cantainer}>
      <div className={styles.Box}>
        <Title name="login" />
        <input type="text" className={styles.inputSize1} name="input1"></input>
        <input
          type="password"
          className={styles.inputSize2}
          name="input2"
        ></input>
        <br></br>
        <a href="#">注册</a>
        <br></br>
        <button
          className={styles.button1}
          type="submit"
          onClick={inputUsername}
        >
          登入
        </button>
      </div>
    </div>
  );
};

export default LoginPage;
